INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (97, NOW(), 28, 'Internship 2017 i Deloitte AS', 'Vil du finne ut hvordan det egentlig er å jobbe som rådgiver eller revisor? Vil du ha spennende utfordringer og utvikle deg som person? Har du noen uker i januar der det er litt roligere på skolen, eller ser du etter en utfordrende sommerjobb?', '**Vil du finne ut hvordan det egentlig er å jobbe som rådgiver eller revisor? Vil du ha spennende utfordringer og utvikle deg som person? Har du noen uker i januar der det er litt roligere på skolen, eller ser du etter en utfordrende sommerjobb? I Deloitte er det mulig å ha internship enten på sommeren eller under årsoppgjøret i januar.**

