INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (206, NOW(), 3, 'Deltidsstilling som teknisk tester', 'Studerer du IT og ønsker en super-relevant deltidsjobb?', 'Studerer du IT og ønsker en super-relevant deltidsjobb? Er du opptatt av gode kundeopplevelser? Nå har du sjansen til å kvalitetssikre funksjonaliteten i våre mobilapplikasjoner ved hjelp av teknisk testing. Vi søker to studenter som er gode til å skrive, er nøyaktige og som ønsker å bli knallgode på testing! 
 
