INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (541, NOW(), 66, 'Vil du utvikle løsninger for Norges viktigste infrastruktur?', 'Bli med på det tverrfaglige sommerprosjektet KUBE!', '**Bli med på det tverrfaglige sommerprosjektet KUBE!**

