INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (219, NOW(), 33, 'Vil du kickstarte din karriere med målet om å bli markedets beste konsulent?', 'Acando Norge søker morgendagens konsulentstjerner til talentprogram!', 'Acando er et brukerfokusert og teknologidrevet konsulentselskap. Teknologi driver forandring, men
