INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (423, NOW(), 156, 'Summer Internships 2020', 'We are hiring a team of summer interns to join our team in Oslo for the summer 2020.', '**About Heimdall Power**  
