INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (563, NOW(), 199, 'Internship innen systemutvikling', 'Postnord utvikler fremtidens logistikkløsninger og ønsker dyktige IT-studenter til Åndalsnes!', 'Søknadsfrist: 01.03.2021

