INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (570, NOW(), 203, 'Sommerjobb med mulighet for påfølgende prosjekt- og masteroppgave', 'Adigo Mekatonikk ser etter en dyktig student som går i 4. klasse på informatikk, datateknikk eller lignende til sommerjobb med mulighet for påfølgende prosjekt- og masteroppgave.', 'Adigo Mekatonikk er spesialisert på utvikling av høyteknologiske produkter i tverrfaglige team, og nå ser vi etter en dyktig student som går i 4. klasse på informatikk, datateknikk eller lignende til sommerjobb med mulighet for påfølgende prosjekt- og masteroppgave. Vi har flere spennende prosjekter i pipeline og du kan være med å forme en morsom, spennende og utfordrende prosjekt- og masteroppgave innenfor et av disse prosjektene så oppgaven passer med dine interesser.

