INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (227, NOW(), 91, 'Vil du bli konsulent?', 'Kodeworks er et lite konsulenthus som nå søker flere konsulenter til våre spennende kundeprosjekter.', 'Kodeworks er et lite konsulenthus som nå søker flere konsulenter til våre spennende kundeprosjekter. For våre kunder gjør vi små og store leveranser innen web, mobil, sikkerhet og IoT. Hos oss får du brukt din fagkunnskap, kreativitet og samarbeidsevner til å utvikle gode, gjennomtenkte IT-løsninger.

