INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (233, NOW(), 60, 'Spennende jobbmulighet', 'Are you a team member, with the ability to work across disciplines? Do you appreciate well-designed, coherent, and documented code? Are you outgoing and structured, excited for the opportunity to push the boundaries of video?', '### We are looking for talented software engineers to join one of our development teams
