INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (382, NOW(), 71, 'Junioranalytiker', 'Analyseseksjonene jobber på tvers av bransjer og med samarbeidspartnere både internt og eksternt. Som analytiker hos oss har du ansvar for oppfølging av kundeporteføljer, samt støtteaktiviteter som bidrar til at DNB når sine mål.', '# Junioranalytiker - spennende karrieremulighet for nyutdannede

