INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (470, NOW(), 5, 'Ønsker du som nyutdannet 2021 å jobbe med de mest spennende teknologiene? Søk Ignite', '“IgnITe er de unge talentene som gir energi til våre kunder med nye ideer og engasjement. Vi
