INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (240, NOW(), 98, 'Junior Quantitative Analyst', 'We are looking for a Junior Quantitative Analyst, who is keen to learn the trading and portfolio management business. The right candidate will be performing own trades in an inspiring and challenging environment.', 'Statkraft is a leading company in hydropower internationally and Europe’s largest generator of renewable energy. The Group produces hydropower, wind power, solar power, gas-fired power and supplies district heating. Statkraft is a global company in energy market operations. Statkraft has 3400 employees in 16 countries.


