INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (253, NOW(), 7, 'Graduate 2019', 'Brenner du for å utvikle?  Søknadsfrist 30 september', '### Nyutdannet i 2019 og brenner for å utvikle?

