INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (428, NOW(), 14, 'Sommerjobb for utviklere i Trondheim', 'Nysgjerrig på hvordan det er å jobbe som utvikler? Vi har ledig sommerjobb for utviklere ved
