INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (300, NOW(), 121, 'Oppdrag innen programvareutvikling', 'Voksenopplæringsforbundet søker 2 studenter til oppdrag innen programvareutvikling', 'Vi er i gang med et pilotprosjekt for å bygge et nettbasert fagsystem for kursregistrering og tilskuddsforvaltning for våre studieforbund. For å styrke arbeidet søker vi 1-2 personer som kan bidra til arbeidet med å ferdigstille prototypen til systemet.  
