INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (264, NOW(), 104, 'Sommerjobb i et nyoppstartet konsulentselskap?', 'Vi ser etter hyggelige studenter som ønsker seg en spennende og utfordrende sommerjobb!', '**Synes du entreprenørskap kombinert med jobb som konsulent virker spennende? Progressive IT (ProgIT) er et nyoppstartet – litt annerledes – konsulentselskap med store ambisjoner. Vi ser nå etter hyggelige studenter som ønsker seg en spennende og utfordrende sommerjobb! Du får muligheten til å være med og forme selskapet og din egen sommer. I ProgIT står frihet og ansvar sentralt med et stort fokus på individuell kompetanseheving.**


