INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (491, NOW(), 177, 'Tech Intern - Summer / Autumn 2021', 'The startup digitizing the world''s keys. Help us open doors — for everyone, everywhere.', 'The startup digitizing the world''s keys. Help us open doors — for everyone, everywhere.

