INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (387, NOW(), 57, 'Sommerjobb hos Bouvet Øst 2020', 'Vi ønsker oss å gi deg som studerende systemutvikler eller designer muligheten til å bli med på vårt sommerjobb-program for å prøve deg på hverdagen som konsulent.', 'På vårt sommerjobb-program får du teste ut hvordan det er å være innafor i Bouvet, hvordan konsulentrollen fungerer i praksis, og du får være med å arbeide på reelle og innovative prosjekter, hos spreke kunder og, gjennom det, ta del i både digitaliseringen og utformingen av fremtidens løsninger.

