INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (286, NOW(), 114, 'Sommerjobb 2019 hos Folq', 'Vi søker deg som sommeren 2019 har fullført 3. eller 4. årstrinn, og kan jobbe full tid 6-8 uker sommeren 2019.', '### Hvem er vi?
