INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (389, NOW(), 145, 'Orkla IT - Summer Internship 2020', 'Søk sommerjobb i Orkla IT!', '**Orkla Summer Academy: Internships - Not your typical summer camp…**  

