INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (524, NOW(), 46, 'Graduate Software Engineer', 'Do you want to change and improve the way people work together?', '**Do you want to change and improve the way people work together?**

  
