INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (305, NOW(), 122, 'Sommerjobb for IT-studenter', 'Er du IT-student og har fullført andre året på en bachelor eller tar en master?
Vi har to spennende sommerjobber ledig ved avdeling for Innovasjon og utvikling.', 'Er du IT-student og har fullført andre året på en bachelor eller tar en master?
Vi har to spennende sommerjobber ledig ved avdeling for Innovasjon og utvikling.

