INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (311, NOW(), 60, 'Appear TV is looking for developers', 'The rebirth of TV puts software front and center. Appear TV look to industry trends, and work in lean product teams that work on specific products. We are continuously looking for talented developers for the next TV revolution.', '**Who are you?**
