INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (424, NOW(), 157, 'SAP-trainee med oppstart sommeren 2020', 'Som SAP-trainee i Coop Norge vil du ta del i et skreddersydd program som over to år vil gi deg ekspertise innen det mest komplette IT-verktøyet i markedet. Oppstart for programmet er i august 2020.', 'Traineeprogrammet består av **fem rulleringer** der du får jobbe tett med hele vår verdikjede, fra produksjon, transport,  lager til butikk, i tillegg til markedsføring, finans og analytics, både innenfor dagligvare, faghandel og e-handel.

 

