INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (334, NOW(), 134, 'React-utvikler', 'Telenor Software Lab i Trondheim utvikler Telenors tjeneste “Min Sky” for skylagring av bilder og
video. Tjenesten har over en million brukere og er en sentral del av Telenors satsing på digitale
tjenester.', 'TL;DR:

