INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (439, NOW(), 87, 'Summer internship at at Oliasoft', 'Are you a passionate software developer who love a challenge? You could be the new intern we are looking for!', '### Are you a passionate software developer who love a challenge? You could be the new intern we are looking for!

