INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (337, NOW(), 137, 'Frontend utviklere søkes', 'Har du lyst til å jobbe som frontend utvikler med spennende teknologier, da søker vi deg!', 'Vi søker etter 2 frontend utviklere som kan bidra til utvikle Moen Marins moderne web baserte system ved navn mLINK. mLINK benytter i dag Vue.js rammeverket der vi også benytter oss av Material Design utviklet av Google. Frontend er en såkalt SPA (Single-Page Application) som benytter REST API’er fra vår Google Cloud hostede backend.

  

