INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (446, NOW(), 149, 'Technical Support Consultant', 'We are looking for a technical consultant to our Support & Operation team in the business area of Smart Energy. The role is based  in Trondheim.', '#### What are we looking for?

