INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (344, NOW(), 6, 'Er du nyutdannet til jul? Bli IT-konsulent!', 'Bli med på å utvikle løsninger som betyr noe for samfunnet!', 'Hvert år rekrutterer vi dyktige nyutdannede som er engasjert og nysgjerrig på ny teknologi!

