INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (484, NOW(), 175, 'Programvareutvikler eller interaksjonsdesigner i start-up?', 'Realfagshjelpen er en start-up med utspring fra NTNU i Trondheim. Vi tilbyr én-til-én- og gruppeundervisning i Trondheim, Bergen, Oslo og på nett. Alle i selskapet er studenter: lærere, driftsteam og eiere. Høsten 2020 påbegynnes en satsning på en ny', 'Realfagshjelpen er en start-up med utspring fra NTNU i Trondheim. Vi tilbyr én-til-én- og gruppeundervisning i Trondheim, Bergen, Oslo og på nett. Alle i selskapet er studenter: lærere, driftsteam og eiere. Høsten 2020 påbegynnes en satsning på en ny, digital læringsplattform. Vi er i en spennende fase preget av sterk vekst og satsning på et helt nytt innovasjonsprosjekt. Derfor søker vi  **programvareutviklere og en interaksjonsdesigner.**  
  

