INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (467, NOW(), 142, 'Sommerjobb hos Bredvid', 'Vil du prøve deg som konsulent hos et ungt og spennende selskap? Bredvid søker studenter som ønsker erfaring om hvordan det er å være konsulent!', 'Vil du prøve deg som konsulent hos et ungt og spennende selskap?
