INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (487, NOW(), 25, 'Er du nyutdannet og ønsker en karriere innen utvikling eller test?', 'Ønsker du å jobbe i et av Norges mest innovative selskaper? Vi søker deg som er nyutdannet høsten 2021 til
