INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (565, NOW(), 89, 'Diverse Internships hos Gjensidige', 'Gjensidige er på jakt etter talenter til vår internshipordning. Er det deg vi leter etter? Internshipet passer for deg ønsker å jobbe deltid ved siden av studiene gjennom et helt studieår.', 'Som intern hos oss vil du skaffe deg relevant arbeidserfaring og samtidig få en unik forståelse for hva du har i vente etter studiene. Du får selvfølgelig også lønn! 

