INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (536, NOW(), 109, 'NorgesGruppen IT og Digitale tjenester søker nyutdannede teknologer/analytikere', 'Er du snart ferdig med din master-grad? Eller har du akkurat blitt ferdig?
Vil du gjøre noe matnyttig med din kompetanse?
