INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (579, NOW(), 210, 'Internship innenfor dataanalyse', 'Nyskjerrig på yrkeslivet? Ser du etter muligheten for å bruke dine analyse ferdigheter?', '## Nyskjerrig på yrkeslivet? Ser du etter muligheten for å bruke dine analyse ferdigheter?

