INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (582, NOW(), 13, 'Fra smart student til superkonsulent', 'NXT er en avdeling skreddersydd for nyutdannede hvor målet er å sørge for at nyutdannede får en skikkelig bra start på karrieren.', 'NXT er en avdeling skreddersydd for nyutdannede hvor målet er å sørge for at nyutdannede får en skikkelig bra start på karrieren. Gjennom et toårig program vil ca 20 nyutdannede få mulighet til å utforske ulike fagfelt og roller, før du – i samarbeid med din leder, finner ut hvilken fagavdeling som passer deg på sikt.

