INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (613, NOW(), 181, 'We are looking for tech graduates to our specialist track in Norway!', 'TietoEVRY is the leading digital services and software company in the Nordics. We create digital advantages for businesses and societies. Whether you want to become a strategist, coder, analyst, or a future-enthusiast, you will find your place here.', '**Are you ready to shape the future?** 

