INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (589, NOW(), 53, 'Blank søker nyutdannede teknologer for oppstart i 2022', 'Blank er et ambisiøst og litt annerledes design- og teknologimiljø. Nå ønsker vi oss flere nyutdannede teknologer for oppstart i 2022.', 'Blank er et ambisiøst og litt annerledes design- og teknologimiljø. Vi er et felleskap av omtrent 50 svært dyktige designere og teknologer i et konsulentselskap hvor full åpenhet og transparens er en selvfølge. Vi holder til i Youngs gate 7, rett overfor Kulturhuset på Youngstorget.

