INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (598, NOW(), 37, 'Sommerjobb 2022 - NoA Ignite', 'Vil du være sommerstudent hos en av Norges ledende digitale konsulenthus? NoA Ignite er på utkikk etter studenter som gjerne vil bruke litt av ferien sin på å opparbeide seg verdifull arbeidserfaring!', 'NoA Ignite  (tidligere Making Waves) er det største selskapet i [The North Alliance](https://thenorthalliance.com/)  — en progressiv byråfamilie med flere av de fremste strategi-, design-, teknologi- og kommunikasjonsselskapene i Norden.

  
