INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (597, NOW(), 20, 'Analytics-konsulent i traineeprogrammet Nytt Krutt', 'Vil du jobbe i skjæringspunktet mellom IT og business? Visma bWise er et selskap i Vismakonsernet. I Visma bWise har vi det sterkeste fagmiljøet og leverer de viktigste prosjektene innenfor Analytics og BI.', '### Vil du jobbe i skjæringspunktet mellom IT og business?

