INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (604, NOW(), 164, 'Vil du jobbe deltid som utvikler i Tise?', 'Vi er på utkikk etter to studenter høsten 2021, med mulighet for å forlenge til våren 2022. Er dere to som kunne tenke dere å jobbe sammen? Det er også lov å søke som et team!', 'Tise er en sosial markedsplass for kjøp og salg av mote. Vi har gått fra å være en idé på Gløshaugen i 2014, til å være en bedrift med 50 ansatte og 2 millioner brukere i 2021. Dette har vi blant annet fått til ved å ha full kontroll på teknologien selv, og ansette engasjerte og flinke utviklere som brenner for å gjøre verden litt mer miljøvennlig.

