INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (611, NOW(), 173, 'Consultant - Developer (Graduate 2022)', 'I Avo får du muligheten til å være med å sette retning for og forme et ungt selskap i vekst og stadig utvikling, som setter innovasjon i førersetet.', '**Hvem er vi?**

