INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (616, NOW(), 20, 'Tech Talent - Software Engineer Program', 'Are you interested in having a career as a software engineer where you can impact millions of end-users around Europe?', 'Are you interested in having a career as a software engineer where you can impact millions of end-users around Europe? How about having the flexibility to innovate and challenge the status quo? Are you a graduate that would like to be part of a workplace where you are encouraged to create innovation and shape the future of society through technology? In that case, Visma’s Tech Talent - Software Engineer Program is the perfect program for you.

