INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (634, NOW(), 2, 'Sommerjobb i Knowit 2022', 'Vi gjentar suksessen fra tidligere år og inviterer bachelor- og master-studenter til å jobbe for oss sommeren 2022. Vi er på jakt etter studenter som vil være med og utvikle digitale løsninger for kunder som Ruter, Entur, Kartverket sammen med oss.', '# Sommerjobb i Knowit 2022

