INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (638, NOW(), 186, 'Sommerjobb i Fink!', 'Bli en Fink!
Vi ser etter flinke og hyggelige studenter som vil teste ut konsulentlivet hos oss mellom 27. juni og 5. August.', '**Hvem ser dere etter?**
