INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (637, NOW(), 222, 'Nyutdannede utviklere 2022', 'Har du lyst til å starte din karriere i et ungt og spennende konsulentselskap som tilbyr god opplæring og varierte prosjekter? Hos oss vil du som nyutdannet få opplæring i metodikk og teknologi, samtidig som du får praktisk erfaring med å jobbe som k', 'Som nyutdannet hos oss vil du ta del i vårt opplæringsprogram som vil foregå på våre kontorer både i Oslo og Krakow. Her vil det bli gitt opplæring i hvordan Fortes ansatte jobber sammen i tverrfaglige prosjekter, samt opplæring i de viktigste teknologiene vi jobber med. Som nyutdannet får du en egen mentor som vil følge deg tett og gi den nødvendige oppfølgingen. Mentoren vil blant annet bistå deg faglig og dere vil jobbe i samme prosjekt slik at du som nyutdannet får relevant og praktisk erfaring.

