INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (646, NOW(), 112, 'Graduates look here', 'Start your career with DNV and have a meaningful job with one of Norway’s most attractive employers.', 'Are you fresh out of university, or soon to complete your degree? 


