INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (647, NOW(), 7, 'Summer internship 2022', 'Vi ser etter motiverte studenter som ønsker å tilbringe sommeren på våre kontorer!', '## Jobbeskrivelse  

