INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (661, NOW(), 48, 'ITverket søker nyutdannede', 'Ferdig med studiene snart? ITverket søker nyutdannede for oppstart høsten 2022.', 'ITverket er et konsulentselskap lokalisert i Oslo og Drammen med 116 engasjerte ansatte. Vi har solide fagmiljø innen Java, .Net og frontendteknologier, og satser friskt på mobilutvikling og cloud fremover. Vi lever etter våre verdier som sier at vi skal være «pålitelige, lekne og fleksible» både overfor våre kunder, men også internt. Vi setter mennesket i fokus og mener at en fornøyd ansatt er en produktiv ansatt.

