INSERT INTO job_listing (id, "createdAt", "companyId", title, ingress, description, start, "end", featured, deadline, "applicationLink", "applicationEmail", employment, "deadlineAsap", hidden) VALUES (669, NOW(), 89, 'Kickstart din karriere med et summerinternship i Gjensidige!', 'Er du opptatt av digitalisering og teknologiske løsninger? Neste sommer har du muligheten til å få et innblikk i hvordan vi jobber med å utvikle digitale løsninger innen forsikring!', '**Er du opptatt av digitalisering og teknologiske løsninger? Neste sommer har du muligheten til å få et innblikk i hvordan vi jobber med å utvikle digitale løsninger innen forsikring!**

